require('chai').should()

const {
  getShieldingWithdrawInputError,
} = require('../src/validator')

describe('Validator', () => {
  describe('#getShieldingWithdrawInputError', () => {
    it('should work', () => {
      getShieldingWithdrawInputError(withdrawData)
    })

    it('should throw for incorrect proof', () => {
      const malformedData = { ...withdrawData }
      malformedData.proof = '0xbeef'
      getShieldingWithdrawInputError(malformedData).should.be.equal(
        '.proof should match pattern "^0x[a-fA-F0-9]{512}$"',
      )
    })

    it('should throw something is missing', () => {
      const malformedData = { ...withdrawData }
      delete malformedData.proof
      getShieldingWithdrawInputError(malformedData).should.be.equal(" should have required property 'proof'")
      malformedData.proof = withdrawData.proof

      // delete malformedData.args
      // getShieldingWithdrawInputError(malformedData).should.be.equal(" should have required property 'args'")
      // malformedData.args = withdrawData.args

      // delete malformedData.contract
      // getShieldingWithdrawInputError(malformedData).should.be.equal(" should have required property 'contract'")
      // malformedData.contract = withdrawData.contract
    })
  })
})

